// This code removes actual newline characters (line breaks).

const fieldName = 'output'; // <-- CHANGE THIS to your field name

for (const item of items) {
  const text = item.json[fieldName];

  if (typeof text === 'string') {
    // Use a regular expression /\\n/g to find all newline characters
    const cleanedText = text.replace(/\n/g, '');

    // Overwrite the original field
    item.json[fieldName] = cleanedText;
  }
}

return items;
