// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  const messyText = item.json.text;

  // Check if the 'text' field exists and is a string
  if (typeof messyText === 'string') {
    // 1. Replace all newline characters with a single space
    let cleanedText = messyText.replace(/(\r\n|\n|\r)/gm, " ");

    // 2. Remove any character that is NOT a letter, number, space, comma, or full stop
    cleanedText = cleanedText.replace(/[^a-zA-Z0-9 ,.]/g, "");

    // 3. Tidy up by replacing multiple spaces with a single space and trimming ends
    cleanedText = cleanedText.replace(/\s+/g, ' ').trim();

    // Add the cleaned text to the new field
    item.json.myNewField = cleanedText;

  } else {
    // If the 'text' field is missing or not text, add an error message
    item.json.myNewField = "Error: Input field 'text' not found or is not a string.";
  }
}

return $input.all();

